if (A = B) then
    l := (3 * A[1]^2) / (2 * A[2]);
else if (A = -B) then
    return P[1] - A[1];
else
    l := (B[2] - A[2]) / (B[1] - A[1]);
end if;
return (l * (P[1] -A[1]) + A[2] -P[2]);

